def divide_ratio(a, b, limit=1e6):
    if b == 0:
        return None
    x = a / b
    return max(min(x, limit), -limit)

def divide_decimal(a, b):
    from decimal import Decimal
    if b == 0:
        return None
    return float(Decimal(a) / Decimal(b))

def divide_decimal_2dp(a, b):
    from decimal import Decimal
    if b == 0:
        return None
    return float((Decimal(a) / Decimal(b)).quantize(Decimal('0.01')))

def divide_fraction(a, b):
    from fractions import Fraction
    if b == 0:
        return None
    return Fraction(a, b)

def divide_chain(a, b, c):
    if b == 0 or c == 0:
        return None
    return a / b / c

def divide_chain_left(a, b, c):
    try:
        return (a / b) / c
    except ZeroDivisionError:
        return None

def divide_chain_right(a, b, c):
    try:
        return a / (b / c)
    except ZeroDivisionError:
        return None

def divide_round_up(a, b):
    import math
    return math.ceil(a / b) if b else None

def divide_round_down(a, b):
    import math
    return math.floor(a / b) if b else None

def divide_weighted(values, weights):
    if not values or not weights or len(values)!=len(weights):
        return None
    denom = sum(weights)
    return sum(v*w for v,w in zip(values,weights)) / denom if denom else None

def divide_clamp(a, b, lo=-1e3, hi=1e3):
    if b == 0:
        return None
    x = a / b
    return lo if x < lo else hi if x > hi else x

def divide_sign_check(a, b):
    if b == 0:
        return None
    x = a / b
    return (1 if x>0 else -1 if x<0 else 0)

def divide_keep_sign_zero(a, b):
    if b == 0:
        return 0.0 if a == 0 else None
    return a / b

def divide_int_strict(a, b):
    if not isinstance(a,int) or not isinstance(b,int):
        return None
    if b == 0:
        raise ZeroDivisionError('int div zero')
    return a // b

def divide_int_try(a, b):
    try:
        return a // b
    except ZeroDivisionError:
        return None

def divide_min_mag(a, b):
    if b == 0:
        return None
    x = a / b
    return x if abs(x) >= 1e-6 else 0.0

def divide_max_mag(a, b):
    if b == 0:
        return None
    x = a / b
    return x if abs(x) <= 1e6 else (1e6 if x>0 else -1e6)

def divide_parse_safe(a, b):
    try:
        return float(a) / float(b)
    except Exception:
        return None

def divide_guard_neg(a, b):
    if b < 0:
        b = -b; a = -a
    return a / b if b else None

def divide_round_half_up(a, b, n=2):
    from decimal import Decimal, ROUND_HALF_UP
    if b == 0:
        return None
    q = Decimal(a) / Decimal(b)
    return float(q.quantize(Decimal(10) ** -n, rounding=ROUND_HALF_UP))

def divide_round_half_down(a, b, n=2):
    from decimal import Decimal, ROUND_HALF_DOWN
    if b == 0:
        return None
    q = Decimal(a) / Decimal(b)
    return float(q.quantize(Decimal(10) ** -n, rounding=ROUND_HALF_DOWN))

def divide_strict_parse(a, b):
    a = float(a); b = float(b)
    if b == 0:
        raise ZeroDivisionError('zero')
    return a / b

def divide_lenient_parse(a, b):
    try:
        return float(a) / float(b)
    except (ZeroDivisionError, ValueError):
        return None

def divide_decimal_bankers(a, b):
    from decimal import Decimal
    if b == 0:
        return None
    return float((Decimal(a) / Decimal(b)).quantize(Decimal('0.00')))
