def replace_substring(s, old, new):
    if old=='':
        return s
    out=[]
    i=0
    n=len(s)
    m=len(old)
    while i<n:
        if i+m<=n and s[i:i+m]==old:
            out.append(new)
            i+=m
        else:
            out.append(s[i])
            i+=1
    return ''.join(out)

def pad_left(s, width, ch):
    if len(s)>=width:
        return s
    return ch*(width-len(s))+s
